*		{box-sizing:border-box;}

h1		{font-size:4em; font-weight:normal; color:#1400ff;}
h2		{font-size:2em; font-weight:normal;}

html,body,div,span,applet,object,iframe,h1,h2,h3,h4,h5,h6,p,blockquote,pre,a,abbr,acronym,address,big,cite,code,del,dfn,em,img,ins,kbd,q,s,samp,small,strike,strong,sub,sup,tt,var,b,u,i,center,dl,dt,dd,ol,ul,li,fieldset,form,label,legend,table,caption,tbody,tfoot,thead,tr,th,td,article,aside,canvas,details,embed,figure,figcaption,footer,header,hgroup,menu,nav,output,ruby,section,summary,time,mark,audio,video{border:0;font-size:100%;font:inherit;vertical-align:top;margin:0;padding:0}article,aside,details,figcaption,figure,footer,header,hgroup,menu,nav,section{display:block}body{line-height:1}ol,ul{list-style:none}blockquote,q{quotes:none}blockquote:before,blockquote:after,q:before,q:after{content:none}table{border-collapse:collapse;border-spacing:0}	/* Resets */

a,img{border:0; outline:none;}textarea:focus,input:focus{outline:0;}*:focus{outline:0;}

html		{-webkit-font-smoothing:antialiased; -moz-osx-font-smoothing:grayscale;}

body, html	{width:100%; margin:auto; padding:0; background-color:#111; font:16px/20px 'Arial', 'Liberation Sans', 'Helvetica Neue', sans-serif; font-size-adjust:0.4; font-weight:normal; font-style:normal; background:url('{_root}data/derp.png')#3C321D; background-attachment:fixed;}
a			{-webkit-transition:all .2s ease-in-out; -moz-transition:all .2s ease-in-out; -o-transition:all .2s ease-in-out; -ms-transition:all .2s ease-in-out; transition:all .2s ease-in-out; cursor:pointer;}
a, img		{border:0; outline:none;}
a:focus		{color:#999; text-decoration:none;}
a:link  	{color:#999; text-decoration:none;}
a:visited	{color:#999; text-decoration:none;}
a:hover		{color:#fff; text-decoration:none;}
a:active	{color:#999; text-decoration:none;}
b			{font-weight:bold;}
i			{font-style:italic;}
h1			{display:none;}

@-moz-document url-prefix() {
    body,html	{font-size:26px;}
}


.nav_wrap	{width:100%; margin:auto;}
.nav		{display:inline-block; vertical-align:top; width:50%; border-bottom:1px dashed #1400ff; padding-bottom:20px;}
.nav:nth-of-type(2)	{text-align:right;}

.page_link	{display:inline-block; width:50%; vertical-align:top; margin-top:80px;}
.page_link:nth-of-type(2)	{text-align:right;}


.default		{width:960px; margin:0 auto;}
.max			{width:100%; margin:0 auto; padding-top:40px; text-align:center;}
.max.bot		{padding-bottom:220px;}

.day			{margin:0 auto; width:680px; position:relative; padding-bottom:10px; margin-top:60px;}
.date			{position:absolute; bottom:0; right:0; color:#d9f7c9; font-size:0.8em;}
.peak_wrap		{margin:0 auto;}


/*	Style for blog posts
	============================================= */

.peak_box		{margin:0 auto; width:680px; background-color:#E9ECDA; padding:30px 40px 40px 40px; color:#171715; font-family:monospace; font-size:1.1em; line-height:1.6em; border-top:2px solid #920000; box-shadow:0px 10px 100px rgba(204,208,142,0.7);}

.peak_box::first-letter {color:#920000; font-size:2.3em; float:left; padding-top:3px; padding-right:8px; padding-left:3px; line-height:0.8em; font-family:Georgia; font-weight:bold; text-decoration:underline;}

.peak			{margin:0 auto; width:0; height:0; border-left:20px solid transparent; border-right:20px solid transparent; border-top:20px solid #E9ECDA;}

.peak_box img	{border:2px #A9A191; border-style:double dotted; box-sizing:border-box;}
span.caption	{font-style:italic; font-size:0.9em; background-color:#CCD08E; text-align:center; display:block; padding:5px 15px; margin:0 auto; margin-bottom:25px;}

.scroll			{width:700px; background-color:#EBF1CF; height:23px; position:absolute; top:-23px; margin:0 auto; left:-10px; box-shadow:0px 10px 15px rgba(53,37,37,0.5); border-radius:4px; z-index:1;}

.scroll_tip		{height:15px; width:30px; background-color:#6B4E43; position:absolute; top:-19px; left:-35px; border-radius:5px;}
.scroll_tip.a	{left:initial; right:-35px;}


a.top			{display:block; margin-top:40px; color:#74D186;}
a.top:hover		{color:#6767E8;}


.a_blog:nth-child(4n) .bards{margin:0 auto; text-align:center; width:410px; background:url('{_root}data/bards2.png')center center no-repeat; height:160px; background-position-y:-30px;}
.a_blog:nth-child(4n+1) .bards{margin:0 auto; text-align:center; width:410px; background:url('{_root}data/bards3.png')center center no-repeat; height:170px; background-position-y:0px;}
.a_blog:nth-child(4n+2) .bards{margin:0 auto; text-align:center; width:410px; background:url('{_root}data/bards1.png')center center no-repeat; height:170px; background-position-y:0px;}
.a_blog:nth-child(4n+3) .bards{margin:0 auto; text-align:center; width:410px; background:url('{_root}data/bards4.png')center center no-repeat; height:170px; background-position-y:0px;}

table.log			{margin:0 auto; text-align:center; background-color:rgb(12,12,11); box-shadow:0px 0px 50px rgba(12,12,21,0.61); width:960px; table-layout:fixed; margin-top:20px; border-radius:14px;}
table.log tr td		{margin:0 auto; text-align:center; padding:40px;}
table.log tr:nth-type(1) td	{padding-bottom:0;}
table.log tr td	span	{display:block; color:#fff; font-size:0.9em;}
table.log tr td	span i	{display:block; font-size:0.9em; padding-top:5px; color:#aaa;}
table.log tr td span.context_head{margin-top:50px; color:#8787FF; font-size:1.1em;}
table.log tr td span.context_info{margin:auto; margin-top:10px; margin-bottom:10px; width:100%; line-height:1.4em;}


/*	TUWITU
	============================================= */

table.tuwitu			{width:100%; margin:0 auto; text-align:left;}
table.tuwitu tr td		{text-align:left; vertical-align:top; border-bottom:1px solid #111; padding:20px 0;}
table.tuwitu tr td.txt	{padding:20px;}
table.tuwitu tr td:nth-of-type(1)	{width:50px;}
.id						{font-weight:bold; color:#920000;}
.id span				{font-weight:normal; font-style:italic; font-size:0.9em; color:#5D5748;}


/*	SONG LIST
	============================================= */

.song_wrap	{border-top:1px solid #251F12; margin-top:20px; color:#251F12; font-size:0.9em; font-style:italic; line-height:1.4em; padding-top:20px;}
.song		{padding-top:10px; counter-reset:section;}
.song b		{display:block;}
.song span	{display:block; list-style-type:decimal; list-style-position:outside; margin-left:20px; margin:0 auto; padding-bottom:5px;}
.song span:before	{counter-increment:section; content:counter(section) ". ";}


/*	FOOTER ANIMATION
	============================================= */

.runner				{width:100%; margin:0 auto; height:144px; position:relative; overflow:hidden;}
.b0					{position:absolute; width:48px; height:48px; left:0; bottom:0; background:url('{_root}data/leon.gif')center no-repeat; -webkit-animation:run 40s linear infinite; -moz-animation:run 40s linear infinite; -o-animation:run 40s linear infinite;}
.b1					{position:absolute; width:48px; height:72px; left:0; bottom:0; background:url('{_root}data/gwinny.gif')center no-repeat; -webkit-animation:run 43s linear infinite; -moz-animation:run 43s linear infinite; -o-animation:run 43s linear infinite;}
.b2					{position:absolute; width:48px; height:48px; left:0; bottom:0; background:url('{_root}data/ingrid.gif')center no-repeat; -webkit-animation:run 44s linear infinite; -moz-animation:run 44s linear infinite; -o-animation:run 44s linear infinite;}
.b3					{position:absolute; width:48px; height:72px; left:0; bottom:0; background:url('{_root}data/strumm.gif')center no-repeat; -webkit-animation:run 47s linear infinite; -moz-animation:run 47s linear infinite; -o-animation:run 47s linear infinite;}
.b4					{position:absolute; width:48px; height:48px; left:0; bottom:0; background:url('{_root}data/dave.gif')center no-repeat; -webkit-animation:run 48s linear infinite; -moz-animation:run 48s linear infinite; -o-animation:run 48s linear infinite;}
.b5					{position:absolute; width:48px; height:48px; left:0; bottom:0; background:url('{_root}data/gary.gif')center no-repeat; -webkit-animation:run 35s linear infinite; -moz-animation:run 35s linear infinite; -o-animation:run 35s linear infinite;}

.b0:hover:before	{content:"Shh..."; display:block; color:#fff; background-color:#000; padding:5px; font-size:0.8em; position:absolute; top:-40px;}
.b1:hover:before	{content:"♫♪"; display:block; color:#fff; background-color:#000; padding:5px; font-size:0.9em; position:absolute; top:-40px; left:10px;}
.b2:hover:before	{content:"INGRID"; display:block; color:#fff; background-color:#000; padding:5px; font-size:0.7em; position:absolute; top:-40px;}
.b3:hover:before	{content:"𝄢"; display:block; color:#fff; background-color:#000; padding:5px; font-size:1.1em; position:absolute; top:-40px; left:13px; padding-bottom:0;}
.b4:hover:before	{content:"♮♬"; display:block; color:#fff; background-color:#000; padding:5px; font-size:0.9em; position:absolute; top:-40px; left:10px;}
.b5:hover:before	{content:"Traps!"; display:block; color:#fff; background-color:#000; padding:5px; font-size:0.8em; position:absolute; top:-40px;}

.trees				{position:fixed; width:100%; height:144px; left:0; bottom:0; background:url('{_root}data/trees.png')center bottom repeat-x;     z-index:666;}

@keyframes run {0%{margin-left:-100px;}100%{margin-left:1900px;}}
@-webkit-keyframes run {0%{margin-left:-100px;}100%{margin-left:1900px;}}
@-moz-keyframes run {0%{margin-left:-100px;}100%{margin-left:1900px;}}
@-o-keyframes run {0%{margin-left:-100px;}100%{margin-left:1900px;}}


/*	PHONES & STUFF
	============================================= */

@media screen and (max-width: 1024px) {

a.head img		{width:960px;}
.day, .peak_box	{width:880px;}
.scroll			{width:900px;}
.peak_box		{line-height:48px; padding-top:50px;}
.peak_box img	{width:800px;}
.peak_box::first-letter	{font-size:100px; margin-right:10px;}
.date			{font-size:28px;}
.peak			{border-left:40px solid transparent; border-right:40px solid transparent; border-top:40px solid #E9ECDA;}
.song_wrap		{padding-top:40px;}
.song			{padding:40px 0;}

span.caption	{padding:30px 20px; margin-bottom:45px;}
.a_blog:nth-child(4n) .bards, .a_blog:nth-child(4n+1) .bards, 
.a_blog:nth-child(4n+2) .bards, .a_blog:nth-child(4n+3) .bards	{width:600px; height:234px; background-size:contain;}

table.tuwitu tr td:nth-of-type(1)		{width:100px;}
table.tuwitu tr td:nth-of-type(1) img	{width:100px; height:100px;}
table.tuwitu tr td.txt		{font-size:20px; line-height:40px;}
table.tuwitu tr td.txt div	{padding-bottom:10px;}

.bot img		{width:600px;}
table.log		{width:800px;}
table.log tr td	span	{font-size:22px; line-height:38px;}
table.log tr td			{display:block;}
table.log tr:first-of-type td:first-of-type	{padding-top:60px;}
table.log tr:last-of-type td:last-of-type	{padding-bottom:100px;}
table.log tr td span.context_head	{font-size:28px;}
table.log tr td span.context_info	{width:80%; font-size:34px;}

}

