table.log tr:nth-type(1) td	{padding-bottom:0;}
table.log tr td	span	{display:block; color:#fff; font-size:0.9em;}
table.log tr td	span i	{display:block; font-size:0.9em; padding-top:5px; color:#aaa;}
table.log tr td span.context_head{margin-top:50px; color:#8787FF; font-size:1.1em;}
table.log tr td span.context_info{margin:auto; margin-top:10px; margin-bottom:10px; width:100%; line-height:1.4em;}


/*	TUWITU
	============================================= */

table.tuwitu			{width:100%; margin:0 auto; text-align:left;}
table.tuwitu tr td		{text-align:left; vertical-align:top; border-bottom:1px solid #111; padding:20px 0;}
table.tuwitu tr td.txt	{padding:20px;}
table.tuwitu tr td:nth-of-type(1)	{width:50px;}
.id						{font-weight:bold; color:#920000;}
.id span				{font-weight:normal; font-style:italic; font-size:0.9em; color:#5D5748;}


/*	SONG LIST
	============================================= */

.song_wrap	{border-top:1px solid #251F12; margin-top:20px; color:#251F12; font-size:0.9em; font-style:italic; line-height:1.4em; padding-top:20px;}
.song		{padding-top:10px; counter-reset:section;}
.song b		{display:block;}
.song span	{display:block; list-style-type:decimal; list-style-position:outside; margin-left:20px; margin:0 auto; padding-bottom:5px;}
.song span:before	{counter-increment:section; content:counter(section) ". ";}


/*	FOOTER ANIMATION
	============================================= */

.runner				{width:100%; margin:0 auto; height:144px; position:relative; overflow:hidden;}
.b0					{position:absolute; width:48px; height:48px; left:0; bottom:0; background:url('{_root}data/leon.gif')center no-repeat; -webkit-animation:run 40s linear infinite; -moz-animation:run 40s linear infinite; -o-animation:run 40s linear infinite;}
.b1					{position:absolute; width:48px; height:72px; left:0; bottom:0; background:url('{_root}data/gwinny.gif')center no-repeat; -webkit-animation:run 43s linear infinite; -moz-animation:run 43s linear infinite; -o-animation:run 43s linear infinite;}
.b2					{position:absolute; width:48px; height:48px; left:0; bottom:0; background:url('{_root}data/ingrid.gif')center no-repeat; -webkit-animation:run 44s linear infinite; -moz-animation:run 44s linear infinite; -o-animation:run 44s linear infinite;}
.b3					{position:absolute; width:48px; height:72px; left:0; bottom:0; background:url('{_root}data/strumm.gif')center no-repeat; -webkit-animation:run 47s linear infinite; -moz-animation:run 47s linear infinite; -o-animation:run 47s linear infinite;}
.b4					{position:absolute; width:48px; height:48px; left:0; bottom:0; background:url('{_root}data/dave.gif')center no-repeat; -webkit-animation:run 48s linear infinite; -moz-animation:run 48s linear infinite; -o-animation:run 48s linear infinite;}
.b5					{position:absolute; width:48px; height:48px; left:0; bottom:0; background:url('{_root}data/gary.gif')center no-repeat; -webkit-animation:run 35s linear infinite; -moz-animation:run 35s linear infinite; -o-animation:run 35s linear infinite;}

.b0:hover:before	{content:"Shh..."; display:block; color:#fff; background-color:#000; padding:5px; font-size:0.8em; position:absolute; top:-40px;}
.b1:hover:before	{content:"♫♪"; display:block; color:#fff; background-color:#000; padding:5px; font-size:0.9em; position:absolute; top:-40px; left:10px;}
.b2:hover:before	{content:"INGRID"; display:block; color:#fff; background-color:#000; padding:5px; font-size:0.7em; position:absolute; top:-40px;}
.b3:hover:before	{content:"𝄢"; display:block; color:#fff; background-color:#000; padding:5px; font-size:1.1em; position:absolute; top:-40px; left:13px; padding-bottom:0;}
.b4:hover:before	{content:"♮♬"; display:block; color:#fff; background-color:#000; padding:5px; font-size:0.9em; position:absolute; top:-40px; left:10px;}
.b5:hover:before	{content:"Traps!"; display:block; color:#fff; background-color:#000; padding:5px; font-size:0.8em; position:absolute; top:-40px;}

.trees				{position:fixed; width:100%; height:144px; left:0; bottom:0; background:url('{_root}data/trees.png')center bottom repeat-x;     z-index:666;}

